import './_UserCrud.scss'
import React, { Component } from 'react'
import axios from 'axios'

import { Main } from '../index'

const headerProps = {
    icon: 'users',
    title: 'User',
    subtitle: 'Cadastro de usuários: Create, Read, Update and Delete'
}

const baseUrl = 'http://localhost:3001/users'
const initialState = {
    user: { name: '', email: '' },
    list: []
}

class UserCrud extends Component {

    state = { ...initialState }

    clear() {
        this.setState({ user: initialState.user })
    }

    save() { // incluir = nao tem ID  /  alterar = tem ID
        const user = this.state.user
        const method = user.id ? 'put' : 'post'
        const url = user.id ? `${baseUrl}/${user.id}` : baseUrl

        axios[method](url, user)
            .then(resp => {
                const list = this.getUpdatedList(resp.data)
                this.setState({ user: initialState.user, list })
            })
    }

    getUpdatedList(user) {
        const list = this.state.list.filter(u => u.id !== user.id)
        list.unshift(user)
        return list
    }

    updateField(event) {
        const user = { ...this.state.user }
        user[event.target.name] = event.target.value
        this.setState({ user })
    }

    renderForm() {
        return (
            <div className="form">
                <div className="form__row">
                    <div className="form__col">
                        <div className="form__group name">
                            <label>Nome:</label>
                            <input type="text" className="form__group__control"
                                name="name"
                                value={this.state.user.name}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite seu nome..."
                            />
                        </div>
                    </div>

                    <div className="form__col">
                        <div className="form__group email">
                            <label>e-Mail:</label>
                            <input type="email" className="form__group__control"
                                name="e-mail"
                                value={this.state.email}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite seu e-Mail..."
                            />
                        </div>
                    </div>
                </div>

                <div className="form__row">
                    <div className="form__col__btn">
                        <button className="btn btn__save"
                        onClick={e => this.save(e)}>
                            Salvar
                        </button>

                        <button className="btn btn__cancel"
                        onClick={e => this.clear(e)}>
                            Cancelar
                        </button>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        return (
            <Main { ...headerProps }>
                { this.renderForm() }
            </Main>
        )
    }
}

export { UserCrud }
